import { type Dispatch } from 'react';
type Id = string | number;
type Transition = {
    duration: number;
};
export type StackItem = {
    id: Id;
    transition?: Transition;
};
type Action<T extends StackItem> = {
    type: 'push';
    item: T;
} | {
    type: 'pop';
    transition?: Transition;
} | {
    type: 'remove';
    id: Id;
    transition?: Transition;
} | {
    type: 'update';
    item: Partial<T> & StackItem;
};
export type StackDispatch<T extends StackItem> = Dispatch<Action<T>>;
export declare function useStack<T extends StackItem>(initialStack?: T[]): [T[], StackDispatch<T>];
export {};
